mapper.update:
UPDATE MEMBER
SET ADMIN_YN = 'Y',
    MEMBER_NICKNAME = #{newAdminNickname}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET MEMBER_NICKNAME = #{memberNickname}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET MEMBER_PW = #{encodedPw}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME = #{memberName}
  AND EMAIL = #{email}
  AND STATUS_YN = 'Y'
FETCH FIRST 1 ROW ONLY
